Demo.Compiler:
COMPILER=go
FILE=test.go
GOROOT=`go env GOROOT`
debug=false
WORKDIR=$HOME
if [ "$CompilerOption" = "debug" ]; then
	debug=true;
fi
if [ "$IdeLang" = "go" ]; then
	COMPILER=go
fi
printf "\n";

if [ "$IdeFileName" != "" ]; then
	WORKDIR=$(dirname "$IdeFileName");
	FILE=$(basename "$IdeFileName");
else
	echo $0|base64 --decode > "$WORKDIR/$FILE";
fi

cd $WORKDIR;
if [ $? -eq 0 ] && [ -f "$WORKDIR/$FILE" ]; then
	if $debug; then
		go build -gcflags=all="-N -l" -o "$FILE".o $FILE $@;
		echo "add-auto-load-safe-path $GOROOT/src/runtime/runtime-gdb.py" > $HOME/.gdbinit
		gdb "$FILE".o -d "$GOROOT" $@;
	else
    	$COMPILER run $FILE $@;
    fi
    go clean -cache
fi
printf "\n";

if [ "$IdeFileName" != "" ]; then
	cat "$IdeFileName"|jq-repl $@;
else
	echo $0|base64 --decode|jq-repl $@;
fi
printf "\n";

