Demo.Content:
/* Welcome to OpenREPL! */
import java.util.*;

public class HelloWorld {
    public static void main(String[] args) {
        System.out.println("Hello, World!");
    }
}

Demo.Compiler:
COMPILER=javac
JAVA=java
FILE=HelloWorld.java
debug=false
WORKDIR=$HOME
runall=1
if [ "$CompilerOption" = "debug" ]; then
	echo "Debugging not Supported for Java.";
	exit;
fi
printf "\n";
if [ "$IdeFileName" != "" ]; then
	WORKDIR=$(dirname "$IdeFileName");
	FILE=$(basename "$IdeFileName");
	runall=0;
else
	echo $0|base64 --decode > "$WORKDIR/$FILE";
fi
CLASS_NAME="${FILE%.*}"
if [ $? -eq 0 ] && [ -f "$WORKDIR/$FILE" ]; then
	if [ "$runall" -eq 1 ]; then
	    $JAVA "$WORKDIR"/*.java $@;
	else
		cd $WORKDIR;
	    $COMPILER $FILE $@;
    	$JAVA $CLASS_NAME $@;
	fi
fi
printf "\n";

COMPILER=bash
FILE=test.sh
debug=false
WORKDIR=$HOME
if [ "$CompilerOption" = "debug" ]; then
	debug=true;
	COMPILER="bash -x";
fi
printf "\n";
if [ "$IdeFileName" != "" ]; then
	WORKDIR=$(dirname "$IdeFileName");
	FILE=$(basename "$IdeFileName");
else
	echo $0|base64 --decode > "$WORKDIR/$FILE";
fi
if [ $? -eq 0 ] && [ -f "$WORKDIR/$FILE" ]; then
    $COMPILER "$WORKDIR/$FILE" $@;
fi
printf "\n";
sleep 0.05
